# Used by Racc
# The @last_token stored for error_reporting
#
def next_token
	@last_tokens.push @q.first
	if @last_tokens.length > 5
		@last_tokens.shift
	end
	tok = @q.shift
    [ tok.sym, tok.data ]
end

def add_end_indent( indt_len )
    while @lvl.last.spaces > indt_len
        @q.push Token.new( :IEND, @lvl.pop.spaces, @lineno, 1 )
    end
end

